import logo from './logo.svg';
import './App.css';
import countries from 'world-countries';

countries.sort((a,b) => b.area-a.area); //storleksordning
const Filtered = countries.filter(country=> country.name.common !== 'Antarctica'); //Remove Antartica
const Sliced = Filtered.slice(0,15); //Slice Filter, new list!

const CountryInfo = ({data, maxwidth, details}) => {
  console.log(data)
  let ratio = data.area / maxwidth;
  let bredd = (ratio*100) + "%";
  return (
    <><div className="container">


      <p class="name">{data.name.common}</p>  <p class="area">{data.area} km <sup>2</sup> </p>

      <div className= "bar" style={{width: bredd}}> </div>
      <div>details
        ? <p> Capital: {data.capital} Region: {data.subregion}</p>: <p></p>
      </div>
    </div></>

  )
}
function App() {
  console.log(Sliced);

  return (
    <div className="box">
      {
        Sliced.map((country) => <CountryInfo Key={country.cca3} data={country} maxwidth={countries[0].area} details ={i <5 }/>)
      }
    </div>
  );
}



export default App;
