import logo from './logo.svg';
import './App.css';
import countries from 'world-countries';

countries.sort((a,b) => b.area-a.area); //storleksordning
const Filtered = countries.filter(country=> country.name.common !== 'Antarctica'); //Remove Antartica
const Sliced = Filtered.slice(0,15); //Slice Filter, new list!

const CountryInfo = ({data, maxwidth}) => {
  console.log(data)
  let ratio = data.area / maxwidth;
  let bredd = (ratio*100) + "%";
  return (
    <><div className="container">

      <div className = "name">
      <p>{data.name.common}</p> </div> <div className = "area"> <p>{data.area} km <sup>2</sup> </p>
      </div>
      <div className= "bar" style={{width: bredd}}> </div>

    </div></>

  )
}
function App() {
  console.log(Sliced);

  return (
    <div className="box">
      {
        Sliced.map((country) => <CountryInfo Key={country.cca3} data={country} maxwidth={countries[0].area}/>)
      }
    </div>
  );
}



export default App;
